int cell(pos_t p) {
    return grid[p.y][p.x];
}

//Place the entity on the grid and register it in its respective array
void placeEntity(int id, int Y, int X, int entityType){
    if (entityType == FIRST_RESPONDER || entityType == CIVILIAN){
        grid[Y][X] = entityType;
        pos[id].x = X;
        pos[id].y = Y;
    }
    else if(entityType == DRONE){
        posDrone[id].x = X;
        posDrone[id].y = Y;
    }
}

void initCallingHelping(){
    int i;
    for(i=0;i< N_CIVILIANS ;i++){
        helping[i]= DEF_POS;
        calling[i]= DEF_POS;
    }
}

void initFRHelping(){
    int i;
    for(i=0;i< N_FR ;i++){
        FRHelping[i]= DEF_POS;
    }
}

